from Crypto.PublicKey import RSA
from Crypto.Util.number import bytes_to_long, long_to_bytes
import random

FLAG = "HQX{not_an_actual_flag}"
key = RSA.generate(2048, e = random.choice([3,5]))

msg = FLAG.encode()
m = bytes_to_long(msg)
c = pow(m, key.e, key.n)

print("e = {}".format(key.e))
print("n = {}".format(key.n))
print("ct = {}".format(c))

